jQuery(document).ready(function(){REISEN_STORAGE.media_frame=null;REISEN_STORAGE.media_link="";jQuery(".reisen_media_selector").on("click",function(a){reisen_show_media_manager(this);a.preventDefault();return false})});function reisen_show_media_manager(a){REISEN_STORAGE.media_link=jQuery(a);if(REISEN_STORAGE.media_frame){REISEN_STORAGE.media_frame.open();return false}REISEN_STORAGE.media_frame=wp.media({title:REISEN_STORAGE.media_link.data("choose"),library:{type:"image"},multiple:REISEN_STORAGE.media_link.data("multiple")===true?"add":false,button:{text:REISEN_STORAGE.media_link.data("update"),close:true}});REISEN_STORAGE.media_frame.on("select",function(b){var c=jQuery("#"+REISEN_STORAGE.media_link.data("linked-field")).eq(0);var e="";if(REISEN_STORAGE.media_link.data("multiple")===true){REISEN_STORAGE.media_frame.state().get("selection").map(function(f){e+=(e?"\n":"")+f.toJSON().url});var d=c.val();e=d+(d?"\n":"")+e}else{e=REISEN_STORAGE.media_frame.state().get("selection").first().toJSON().url}c.val(e);if(c.siblings("img").length>0){c.siblings("img").attr("src",e)}c.trigger("change")});REISEN_STORAGE.media_frame.open();return false};